    public function getSourceContext()
    {
        return new Source("{% extends 'base.html.twig' %}

{% block title %}Log in!{% endblock %}

{% block content %}
    <div class=\"container-fluid col-md-8\">
        <form method=\"post\">
            {% if error %}
                <div class=\"alert alert-danger\">{{ error.messageKey|trans(error.messageData, 'security') }}</div>
            {% endif %}

            {% if app.user %}
                <div class=\"mb-3\">
                    You are logged in as {{ app.user.username }}, <a href=\"{{ path('app_logout') }}\">Logout</a>
                </div>
            {% endif %}
            <div class=\"container-fluid text-center\">
                <div class=\"title_sign\">
                    <h1 class=\"mb-3 mt-5\">SIGN IN</h1>
                </div>
                <div class=\"input\">
                    <label for=\"inputEmail\">Email</label>
                    <input type=\"email\" value=\"{{ last_username }}\" name=\"email\" id=\"inputEmail\" class=\"form-control\"
                           autocomplete=\"email\" required autofocus>
                    <label for=\"inputPassword\">Password</label>
                    <input type=\"password\" name=\"password\" id=\"inputPassword\" class=\"form-control\"
                           autocomplete=\"current-password\" required>

                    <input type=\"hidden\" name=\"_csrf_token\"
                           value=\"{{ csrf_token('authenticate') }}\"
                    >

                    <button class=\"btn text-white connect mt-3 pt-5\" type=\"submit\">
                        Se connecter
                    </button>
                </div>
            </div>

            <hr>
            <div class=\"reset-register\">
                <p class=\"text-center\"><a href=\"{{ path('reset_password') }}\" style=\"color: #a9b97a;\">Mot de passe oublié ?</a></p>
                <p class=\"text-center\"><a href=\"{{ path('register') }}\" style=\"color: #a9b97a;\">Créer un compte</a></p>
            </div>
        </form>
    </div>
{% endblock %}
", "security/login.html.twig", "/Users/cedricdebroux/Documents/norris_transport_it /Projects/tiny_and_pretty_symfony/Tiny_and_Pretty/templates/security/login.html.twig");
    }
