        return new Source("{% extends 'base.html.twig' %}

{% block title %}Mot de passe oublié - Tiny And Pretty{% endblock %}

{% block content %}
    <div class=\"block-small\">
        {%  for message in app.flashes('notice') %}
          <div class=\"alert alert-info\">{{ message }}</div>
        {% endfor %}
        <form method=\"post\">
            <div class=\"container-fluid text-center mt-5 mb-5\">
                <div class=\"title_sign\">
            <h1 class=\"mb-3 mt-5\">RÉINITIALISER MON MOT DE PASSE</h1>
                </div>
            </div>
            <div class=\"container-fluid text-center\">
                <div class=\"row\">
                    <div class=\"col-6  text-center mx-auto\">
            <label for=\"inputEmail\">Email</label>
            <input type=\"email\" name=\"email\" id=\"inputEmail\" class=\"form-control\" autocomplete=\"email\" placeholder=\"Votre adresse email\" required autofocus>
                    </div>
                </div>
                <button class=\"btn text-white connect mt-3 pt-5\" type=\"submit\" type=\"submit\">
                    Valider
                </button>
            </div>


            <div class=\"container-fluid\">
                <div class=\"row\">
                    <div class=\"col-6  text-center mx-auto\">
            <p class=\"text-center\"> Ou</p>
            <span><a href=\"{{ path('app_login') }}\"  style=\"color:#a9bf7a;\">Vous connecter</a></span>
                    </div>
                </div>
            </div>
        </form>
    </div>
{% endblock %}
", "reset_password/index.html.twig", "/Users/cedricdebroux/Documents/norris_transport_it /Projects/tiny_and_pretty_symfony/Tiny_and_Pretty/templates/reset_password/index.html.twig");
