        return new Source("{% extends 'base.html.twig' %}

{% block title %} ajouter une adresse{% endblock %}

{% block content %}
<div class=\"container-fluid text-center mt-5 mb-5\">
    <div class=\"title_sign\">

        <h1>GÉRER MES ADRESSES</h1>
    </div>
    <div class=\"intro mt-auto mb-5\">

        <a href=\"{{ path('account_address') }}\" style=\"color:#a9bf7a;\">Retour</a>
    </div>
    <hr>
    <div class=\"container-fluid\">
        <div class=\"row\">
            <div class=\"col-6  text-center mx-auto\">
                {{ form_start(form) }}


                <div class=\"col-md-12\">
                    {{ form_row(form.name) }}
                </div>
                <div class=\"col-md-12\">
                    {{ form_row(form.firstname) }}
                </div>
                <div class=\"col-md-12\">
                    {{ form_row(form.lastname) }}
                </div>
                <div class=\"col-md-12\">
                    {{ form_row(form.company) }}
                </div>
                <div class=\"col-md-12\">
                    {{ form_row(form.address) }}
                </div>
                <div class=\"col-md-12\">
                    {{ form_row(form.postal) }}
                </div>
                <div class=\"col-md-12\">
                    {{ form_row(form.city) }}
                </div>
                <div class=\"col-md-12\">
                    {{ form_row(form.country) }}
                </div>
                <div class=\"col-md-12\">
                    {{ form_row(form.phone) }}
                </div>
                <div class=\"col-md-12\">
                    {{ form_end(form) }}
                </div>
            </div>
        </div>
    </div>






    {% endblock %}
